import styles from './ratingBox.module.scss'

type RatingBoxProps = {
  rating: number
  ratingCount: number
}

const countReviews = (number: number = 0) => {
  if (number >= 5) {
    return `${number} opinii`
  } else if ([2, 3, 4].includes(number)) {
    return `${number} opinie`
  } else if (number === 1) {
    return `${number} opinia`
  } else {
    return 'Brak opinii'
  }
}

